/*
  src/components/ImageUploader.js
  Handles image upload and runs OCR via the useOCR hook. Provides a manual
  fallback textarea to edit detected numbers as a simple CSV.
*/
import React, { useState } from 'react'
import { useOCR } from '../hooks/useOCR'

export default function ImageUploader({ onResult }) {
  const [file, setFile] = useState(null)
  const [textFallback, setTextFallback] = useState('')
  const [ocrStatus, setOcrStatus] = useState('')
  const { recognizing, resultText, boxes, recognize, recognizeBest } = useOCR()

  const handleFile = (e) => {
    const f = e.target.files?.[0]
    setFile(f)
  }

  // Helper: cluster tokens into rows by Y coordinate and sort by X
  const tokensToRows = (tokens) => {
    if (!tokens || tokens.length === 0) return []
    // compute centerY and height for each token
    const t = tokens.map(tok => ({
      text: tok.text,
      bbox: tok.bbox,
      cx: (tok.bbox.x0 + tok.bbox.x1) / 2,
      cy: (tok.bbox.y0 + tok.bbox.y1) / 2,
      h: tok.bbox.y1 - tok.bbox.y0
    }))
    // median height
    const heights = t.map(x=>x.h).sort((a,b)=>a-b)
    const medianH = heights[Math.floor(heights.length/2)] || 10
    // cluster by cy gap > medianH * 1.2
    const clusters = []
    t.sort((a,b)=> a.cy - b.cy || a.cx - b.cx)
    for (const item of t) {
      const last = clusters[clusters.length-1]
      if (!last) { clusters.push([item]); continue }
      const lastCy = last.reduce((s,i)=>s+i.cy,0)/last.length
      if (Math.abs(item.cy - lastCy) > medianH * 1.2) {
        clusters.push([item])
      } else {
        last.push(item)
      }
    }
    // sort tokens in each cluster by x and map to numbers
    const rows = clusters.map(cluster => cluster.sort((a,b)=>a.cx - b.cx).map(i => Number(i.text)))
    return rows
  }

  // Try to infer a regular grid (rows x cols) from tokens by simple 1D kmeans on x and y
  const tokensToGrid = (tokens, expectedRows = 10, expectedCols = 10) => {
    if (!tokens || tokens.length === 0) return null
    const pts = tokens.map(t=>({text:t.text, cx:(t.bbox.x0+t.bbox.x1)/2, cy:(t.bbox.y0+t.bbox.y1)/2}))
    const xs = pts.map(p=>p.cx)
    const ys = pts.map(p=>p.cy)
    const kmeans1d = (values, k, iters = 10) => {
      if (!values.length) return []
      const min = Math.min(...values), max = Math.max(...values)
      let centers = Array.from({length:k}, (_,i) => min + (i+0.5)*(max-min)/k)
      for (let it=0; it<iters; it++){
        const groups = Array.from({length:k}, ()=>[])
        for (const v of values){
          let best=0,bd = Math.abs(v-centers[0])
          for (let i=1;i<k;i++){ const d=Math.abs(v-centers[i]); if (d<bd){bd=d;best=i}} 
          groups[best].push(v)
        }
        for (let i=0;i<k;i++){ if (groups[i].length) centers[i] = groups[i].reduce((a,b)=>a+b,0)/groups[i].length }
      }
      return centers
    }

    const xCenters = kmeans1d(xs, expectedCols, 20).sort((a,b)=>a-b)
    const yCenters = kmeans1d(ys, expectedRows, 20).sort((a,b)=>a-b)

    const grid = Array.from({length:expectedRows}, ()=>Array.from({length:expectedCols}, ()=>null))
    for (const p of pts){
      let c=0, cd=Math.abs(p.cx-xCenters[0])
      for (let i=1;i<expectedCols;i++){ const d=Math.abs(p.cx-xCenters[i]); if (d<cd){cd=d;c=i}} 
      let r=0, rd=Math.abs(p.cy-yCenters[0])
      for (let j=1;j<expectedRows;j++){ const d=Math.abs(p.cy-yCenters[j]); if (d<rd){rd=d;r=j}} 
      if (!grid[r][c]) grid[r][c] = {text:p.text, dist: Math.hypot(p.cx-xCenters[c], p.cy-yCenters[r])}
      else { const d = Math.hypot(p.cx-xCenters[c], p.cy-yCenters[r]); if (d < grid[r][c].dist) grid[r][c] = {text:p.text, dist:d} }
    }
    const rowsOut = grid.map(row => row.map(cell => cell ? Number(cell.text) : null))
    return rowsOut
  }

  const doOCR = async () => {
    if (!file) return
    // Prefer the best PSM result which runs a few segmentation modes and picks the most likely tokens
    const best = typeof recognizeBest === 'function' ? await recognizeBest(file) : await recognize(file)
    const { text, boxes: ocrBoxes, tokens } = best
    const imageUrl = URL.createObjectURL(file)

    // Parse simple rows from text: split lines, find numbers
    let rows = text.trim().split(/\r?\n/).map(line => {
      const nums = line.match(/\d+/g)
      return nums ? nums.map(n => Number(n)) : []
    }).filter(r => r.length > 0)

    const boxesToUse = (tokens && tokens.length > 0) ? tokens : ocrBoxes

    // If textual rows are empty but we have tokens, attempt to cluster tokens into rows
    if (rows.length === 0 && boxesToUse && boxesToUse.length > 0) {
      const tokenRows = tokensToRows(boxesToUse)
      if (tokenRows.length > 0) {
        rows = tokenRows
      } else {
        // attempt a grid inference (10x10 default)
        const grid = tokensToGrid(boxesToUse, 10, 10)
        if (grid) {
          // compact rows (remove nulls)
          const compact = grid.map(r => r.filter(x=>x!==null))
          if (compact.some(r => r.length > 0)) {
            rows = compact
            setOcrStatus(`Inferred grid with ${compact.length} rows (compacted)`)
          }
        }
        // Try smart fill inference to detect grid & fill missing numbers
        try {
          const { inferAndFill } = await import('../utils/smartFill')
          const smart = inferAndFill(boxesToUse)
          if (smart && smart.rows) {
            rows = smart.rows.map(r => r.map(v => v === null ? null : Number(v)))
            // pass along cellBoxes so CardView can render exact cell overlays
            boxesToUse = smart.cellBoxes.flatMap((row, r) => row.map((b,c)=> ({ text: String(rows[r][c] || ''), bbox: b, r, c })))
            setOcrStatus(`Smart-filled grid (${smart.filledCount} filled) orientation=${smart.orientation}`)
          }
        } catch (e) {
          console.warn('Smart fill failed', e)
        }
      }
    }

    console.log('doOCR: rows detected:', rows.length, 'tokens:', (tokens||[]).length, 'boxes:', (ocrBoxes||[]).length)

    if (rows.length > 0) {
      setOcrStatus(`Parsed ${rows.length} rows from OCR`)
      onResult({ rows, boxes: boxesToUse, imageUrl })
    } else {
      setOcrStatus('No rows detected from OCR; showing raw text in fallback')
      setTextFallback(text)
    }
  }

  const submitFallback = () => {
    // Expect CSV per line
    const rows = textFallback.trim().split(/\r?\n/).map(line => line.split(/[,\s]+/).map(n => n ? Number(n) : null))
    onResult({ rows, boxes: [], imageUrl: file ? URL.createObjectURL(file) : null })
  }

  return (
    <div className="uploader">
      <h3>Upload Bingo Card Image</h3>
      <input type="file" accept="image/*" onChange={handleFile} />
      <div style={{marginTop:8}}>
        <button onClick={doOCR} disabled={!file || recognizing}>{recognizing ? 'Recognizing...' : 'Run OCR'}</button>
        <div style={{marginTop:6,fontSize:12,color:'#666'}}>{ocrStatus}</div>
      </div>
      <div style={{marginTop:12}}>
        <label>OCR raw / manual fallback (CSV per row)</label>
        <textarea value={resultText || textFallback} onChange={(e)=>setTextFallback(e.target.value)} rows={6} style={{width:'100%'}} />
        <div style={{marginTop:6}}>
          <button onClick={submitFallback}>Use Manual Input</button>
        </div>
      </div>
    </div>
  )
}
