/*
  src/components/WinModal.js
  Simple modal that shows a winner row and triggers confetti (confetti called from parent).
*/
import React from 'react'

export default function WinModal({ open, onClose, winner }){
  if(!open) return null
  return (
    <div style={{position:'fixed',inset:0,display:'flex',alignItems:'center',justifyContent:'center',background:'rgba(0,0,0,0.4)'}}>
      <div style={{background:'#fff',color:'#111',padding:20,borderRadius:8,minWidth:260}}>
        <h2>Winner!</h2>
        <div>
          {winner?.player ? (
            <span><strong>{winner.player}</strong> has bingo!</span>
          ) : winner?.axis === 'col' ? (
            <span>Column {winner.index+1} has bingo!</span>
          ) : (
            <span>Row {winner?.index+1} has bingo!</span>
          )}
        </div>
        <div style={{marginTop:12,display:'flex',justifyContent:'flex-end',gap:8}}>
          <button onClick={onClose}>Close</button>
        </div>
      </div>
    </div>
  )
}
