/*
  src/components/CardView.js
  Renders the bingo card as an image with overlaid clickable number boxes.
  Falls back to grid view if no image.
*/
import React, { useState } from 'react'

export default function CardView({ card, players, dabbed, onToggleDab, manualMode, setCard, setDabbed, boxes, imageUrl }){
  const [editing, setEditing] = useState(null) // {r,c} for editing

  if (!card) return <div className="card-view">No card loaded.</div>

  const rows = card.length
  const cols = card[0]?.length || 0

  const updateNumber = (r,c,val) => {
    const next = card.map(row=>row.slice())
    next[r][c] = val === '' ? null : isNaN(Number(val)) ? val : Number(val)
    setCard(next)
  }

  const toggleDabForNumber = (num) => {
    // Find position of num in card
    for (let r = 0; r < card.length; r++) {
      for (let c = 0; c < card[r].length; c++) {
        if (String(card[r][c]) === String(num)) {
          onToggleDab(r, c)
          return
        }
      }
    }
  }

  if (imageUrl && boxes.length > 0) {
    // Image view with overlays scaled to displayed image size
    return (
      <div className="card-view">
        <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
          <h3>Card View</h3>
          <div style={{fontSize:12,color:'#666'}}>Click numbers to dab (manual)</div>
        </div>
        <div style={{position:'relative', display:'inline-block', marginTop:8}}>
          <img
            ref={img => { if (img) { img.dataset.naturalWidth = img.naturalWidth; img.dataset.naturalHeight = img.naturalHeight } }}
            src={imageUrl}
            alt="Bingo Card"
            style={{maxWidth:'100%', height:'auto', display:'block'}}
            onLoad={(e)=>{
              const img = e.currentTarget
              img.dataset.clientWidth = img.clientWidth
              img.dataset.clientHeight = img.clientHeight
            }}
          />
          {boxes.map((box, idx) => {
            const num = box.text
            // Find if this number is dabbed
            let isDabbed = false
            let pos = null
            for (let r = 0; r < card.length; r++) {
              for (let c = 0; c < card[r].length; c++) {
                if (String(card[r][c]) === String(num)) {
                  isDabbed = dabbed?.[r]?.[c] || false
                  pos = {r, c}
                  break
                }
              }
              if (pos) break
            }

            // compute scale based on loaded image element
            // fallback to 1 if not available
            const container = document.querySelector('.card-view img')
            let scaleX = 1, scaleY = 1
            if (container && container.dataset && container.dataset.naturalWidth) {
              const nw = Number(container.dataset.naturalWidth)
              const nh = Number(container.dataset.naturalHeight)
              const cw = Number(container.dataset.clientWidth || container.clientWidth)
              const ch = Number(container.dataset.clientHeight || container.clientHeight)
              scaleX = cw / nw
              scaleY = ch / nh
            }

            const left = (box.bbox.x0 || 0) * scaleX
            const top = (box.bbox.y0 || 0) * scaleY
            const width = (box.bbox.x1 - box.bbox.x0) * scaleX
            const height = (box.bbox.y1 - box.bbox.y0) * scaleY

            return (
              <div
                key={idx}
                style={{
                  position: 'absolute',
                  left: left,
                  top: top,
                  width: Math.max(8, width),
                  height: Math.max(8, height),
                  backgroundColor: isDabbed ? 'rgba(255,0,0,0.5)' : 'rgba(0,128,0,0.25)',
                  border: '1px solid rgba(0,0,0,0.6)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  cursor: 'pointer',
                  fontSize: 12,
                  color: 'white',
                  fontWeight: 'bold',
                  pointerEvents: 'auto'
                }}
                onClick={() => {
                  if (manualMode && pos) onToggleDab(pos.r, pos.c)
                }}
                onDoubleClick={() => {
                  if (pos) setEditing(pos)
                }}
              >
                {num}
              </div>
            )
          })}

          {/* row labels (players) based on box row grouping */}
          {(() => {
            // find annotated row centers from boxes if present
            const rowMap = {}
            boxes.forEach(b => {
              if (typeof b.row === 'number') {
                rowMap[b.row] = rowMap[b.row] || []
                rowMap[b.row].push((b.bbox.y0 + b.bbox.y1) / 2)
              }
            })
            const container = document.querySelector('.card-view img')
            let scale = 1
            if (container && container.dataset && container.dataset.naturalWidth) {
              const nw = Number(container.dataset.naturalWidth)
              const nh = Number(container.dataset.naturalHeight)
              const cw = Number(container.dataset.clientWidth || container.clientWidth)
              scale = cw / nw
            }
            return Object.keys(rowMap).sort((a,b)=>a-b).map((ri) => {
              const avg = rowMap[ri].reduce((s,x)=>s+x,0) / rowMap[ri].length
              const top = avg * scale
              return (
                <div key={ri} style={{position:'absolute', left: -110, top: top - 12, width:100, textAlign:'right', fontSize:12, color:'#222'}}>
                  <strong>{players?.[ri] || `Player ${Number(ri)+1}`}</strong>
                </div>
              )
            })
          })()}

        </div>
        {editing && (
          <div style={{marginTop:8}}>
            <label>Edit number at row {editing.r+1}, col {editing.c+1}:</label>
            <input
              value={card[editing.r][editing.c] ?? ''}
              onChange={(e)=>updateNumber(editing.r, editing.c, e.target.value)}
              onBlur={() => setEditing(null)}
              autoFocus
            />
          </div>
        )}
      </div>
    )
  } else {
    // Fallback grid view
    return (
      <div className="card-view">
        <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
          <h3>Card View</h3>
          <div style={{fontSize:12,color:'#666'}}>Tap numbers to dab (manual)</div>
        </div>
        <div style={{marginTop:8}}>
          {card.map((row, r) => (
            <div key={r} style={{marginBottom:6}}>
              <div className="player-row">
                <strong style={{width:90}}>{players?.[r] || `Row ${r+1}`}</strong>
                <div style={{flex:1,display:'grid',gridTemplateColumns:`repeat(${cols},1fr)`,gap:6}}>
                  {row.map((cell, c) => (
                    <div key={c} className={`cell ${dabbed?.[r]?.[c] ? 'dab' : ''}`} onClick={()=>{ if (manualMode) onToggleDab(r,c) }}>
                      <div>
                        <input value={cell ?? ''} onChange={(e)=>updateNumber(r,c,e.target.value)} style={{width:'100%',border:'none',background:'transparent',textAlign:'center'}} />
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    )
  }
}
