/*
  src/components/CardView.js
  Renders the bingo card as an image with overlaid clickable number boxes.
  Falls back to grid view if no image.
*/
import React, { useState } from 'react'

export default function CardView({ card, players, dabbed, onToggleDab, manualMode, setCard, setDabbed, boxes, imageUrl }){
  const [editing, setEditing] = useState(null) // {r,c} for editing

// Small helper component that reads the image metrics and renders a scaled overlay
function ScaledBox({ box, pos, dabbed, manualMode, onToggle, setEditing }){
  // use ref-less approach: compute position at render time by querying the image DOM
  const img = typeof document !== 'undefined' ? document.getElementById('bingo-card-image') : null
  if (!box || !box.bbox) return null
  let style = { display: 'none' }
  if (img && img.dataset && img.naturalWidth) {
    const nw = +(img.dataset.nw || img.naturalWidth)
    const nh = +(img.dataset.nh || img.naturalHeight)
    const scaleX = rect.width / nw
    const scaleY = rect.height / nh

    // ignore boxes that are too large to be bingo squares (likely player labels or headers)
    const boxW = box.bbox.x1 - box.bbox.x0
    const boxH = box.bbox.y1 - box.bbox.y0
    const maxDim = Math.max(nw, nh)
    // Adjusted thresholds: more strict max size and a small min height to exclude 1-2px artifacts
    const sizeThreshold = maxDim * 0.09 // tuned threshold
    const minHeight = Math.max(6, maxDim * 0.01)
    if (boxW > sizeThreshold || boxH > sizeThreshold || boxH < minHeight) {
      style = { display: 'none' }
    } else {
      style = {
        position: 'absolute',
        left: box.bbox.x0 * scaleX,
        top: box.bbox.y0 * scaleY,
        width: (box.bbox.x1 - box.bbox.x0) * scaleX,
        height: (box.bbox.y1 - box.bbox.y0) * scaleY,
        backgroundColor: dabbed ? 'rgba(128,0,128,0.45)' : 'rgba(128,0,128,0.18)',
        border: '1px solid rgba(0,0,0,0.6)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        cursor: pos && manualMode ? 'pointer' : 'default',
        color: 'white',
        fontWeight: 'bold',
        boxSizing: 'border-box'
      }
    }
  }

  return (
    <div
      style={style}
      onClick={(e) => { e.stopPropagation(); if (pos && manualMode) onToggle() }}
      onDoubleClick={(e) => { e.stopPropagation(); if (pos) setEditing(pos) }}
    >
      {box.text}
    </div>
  )
}

  if (!card) return <div className="card-view">No card loaded.</div>

  const rows = card.length
  const cols = card[0]?.length || 0

  const updateNumber = (r,c,val) => {
    const next = card.map(row=>row.slice())
    next[r][c] = val === '' ? null : isNaN(Number(val)) ? val : Number(val)
    setCard(next)
  }

  const toggleDabForNumber = (num) => {
    // Find position of num in card
    for (let r = 0; r < card.length; r++) {
      for (let c = 0; c < card[r].length; c++) {
        if (String(card[r][c]) === String(num)) {
          onToggleDab(r, c)
          return
        }
      }
    }
  }

  if (imageUrl && boxes.length > 0) {
    // Image view with overlays — measure natural size and scale boxes
    return (
      <div className="card-view">
        <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
          <h3>Card View</h3>
          <div style={{fontSize:12,color:'#666'}}>Click numbers to dab (manual)</div>
        </div>
        <div style={{position:'relative', display:'inline-block', marginTop:8}}>
          <img
            id="bingo-card-image"
            src={imageUrl}
            alt="Bingo Card"
            style={{maxWidth:'100%', height:'auto', display:'block'}}
            onLoad={(e) => {
              const img = e.currentTarget
              img.dataset.nw = img.naturalWidth
              img.dataset.nh = img.naturalHeight
            }}
          />
          {/** Render only boxes that match a number present in `card` and are small enough to be bingo squares. **/}
          {/** If boxes looks like per-cell boxes (has r,c) use that; otherwise fall back to OCR token boxes **/}
          {boxes
            .filter(box => !!box && (box.r !== undefined || (/^[0-9]{1,3}$/.test(box.text))))
            .map((box, idx) => {
              // if box has r/c, use it; otherwise find pos by matching number text
              let pos = null
              let num = box.text
              if (box.r !== undefined && box.c !== undefined) {
                pos = { r: box.r, c: box.c }
                num = String(card[pos.r][pos.c])
              } else {
                for (let r = 0; r < card.length; r++) {
                  for (let c = 0; c < card[r].length; c++) {
                    if (String(card[r][c]) === String(box.text)) { pos = { r, c }; break }
                  }
                  if (pos) break
                }
              }

              // Use the provided bbox (smart fill provides per-cell bboxes)
              const bbox = box.bbox

              return (
                <ScaledBox
                  key={idx}
                  box={{ bbox, text: num }}
                  pos={pos}
                  dabbed={(pos ? dabbed?.[pos.r]?.[pos.c] : false)}
                  manualMode={manualMode}
                  onToggle={() => { if (pos) onToggleDab(pos.r, pos.c) }}
                  setEditing={(p) => { if (p) setEditing(p) }}
                />
              )
            })}
        </div>
        {editing && (
          <div style={{marginTop:8}}>
            <label>Edit number at row {editing.r+1}, col {editing.c+1}:</label>
            <input
              value={card[editing.r][editing.c] ?? ''}
              onChange={(e)=>updateNumber(editing.r, editing.c, e.target.value)}
              onBlur={() => setEditing(null)}
              autoFocus
            />
          </div>
        )}
      </div>
    )
  } else {
    // Fallback grid view
    return (
      <div className="card-view">
        <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
          <h3>Card View</h3>
          <div style={{fontSize:12,color:'#666'}}>Tap numbers to dab (manual)</div>
        </div>
        <div style={{marginTop:8}}>
          {card.map((row, r) => (
            <div key={r} style={{marginBottom:6}}>
              <div className="player-row">
                <strong style={{width:90}}>{players?.[r] || `Row ${r+1}`}</strong>
                <div style={{flex:1,display:'grid',gridTemplateColumns:`repeat(${cols},1fr)`,gap:6}}>
                  {row.map((cell, c) => (
                    <div key={c} className={`cell ${dabbed?.[r]?.[c] ? 'dab' : ''}`} onClick={()=>{ if (manualMode) onToggleDab(r,c) }}>
                      <div>
                        <input value={cell ?? ''} onChange={(e)=>updateNumber(r,c,e.target.value)} style={{width:'100%',border:'none',background:'transparent',textAlign:'center'}} />
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    )
  }
}
