/*
  src/App.js
  Main application container. Coordinates OCR upload, card state,
  ball picker, dab logic, auto/manual modes, and win detection.
*/
import React, { useEffect, useState, useCallback } from 'react'
import ImageUploader from './components/ImageUploader'
import CardView from './components/CardView'
import BallPicker from './components/BallPicker'
import PlayerList from './components/PlayerList'
import WinModal from './components/WinModal'
import { useLocalStorage } from './hooks/useLocalStorage'
import { fireConfetti } from './utils/confetti'

// Keys for localStorage
const LS_KEYS = {
  CARD: 'bingo_card',
  PLAYERS: 'bingo_players',
  DABBED: 'bingo_dabbed',
  CALLED: 'bingo_called',
  SETTINGS: 'bingo_settings'
}

function defaultEmptyCard(rows = 5, cols = 5) {
  // generate an empty card of placeholders (null means empty cell)
  return Array.from({ length: rows }, () => Array.from({ length: cols }, () => null))
}

export default function App() {
  // Persisted state via custom hook
  const [card, setCard] = useLocalStorage(LS_KEYS.CARD, defaultEmptyCard())
  const [players, setPlayers] = useLocalStorage(LS_KEYS.PLAYERS, [])
  const [dabbed, setDabbed] = useLocalStorage(LS_KEYS.DABBED, null)
  const [called, setCalled] = useLocalStorage(LS_KEYS.CALLED, [])
  const [settings, setSettings] = useLocalStorage(LS_KEYS.SETTINGS, { maxBall: 75, autoMode: false })
  const [imageUrl, setImageUrl] = useLocalStorage('bingo_image_url', null)

  const [currentBall, setCurrentBall] = useState(null)
  const [winnerRow, setWinnerRow] = useState(null)

  // ensure dabbed matrix shape matches card
  useEffect(() => {
    if (!card) return
    const rows = card.length
    const cols = card[0]?.length || 0
    if (!dabbed || dabbed.length !== rows || dabbed[0].length !== cols) {
      const newDab = Array.from({ length: rows }, () => Array.from({ length: cols }, () => false))
      setDabbed(newDab)
    }
  }, [card])

  // Try to compute a grid layout from OCR boxes by clustering rows and columns
  const computeGridFromBoxes = (boxes) => {
    if (!boxes || boxes.length === 0) return null
    // augment boxes with centers and sizes
    const pts = boxes.map(b => ({
      ...b,
      num: parseInt(b.text, 10),
      cx: (b.bbox.x0 + b.bbox.x1) / 2,
      cy: (b.bbox.y0 + b.bbox.y1) / 2,
      w: Math.max(1, b.bbox.x1 - b.bbox.x0),
      h: Math.max(1, b.bbox.y1 - b.bbox.y0)
    }))

    // helper median
    const median = (arr) => { const s = arr.slice().sort((a,b)=>a-b); return s[Math.floor(s.length/2)] || s[0] }
    const medianH = median(pts.map(p=>p.h)) || 20
    const medianW = median(pts.map(p=>p.w)) || 20

    // cluster rows by Y
    const rowThreshold = medianH * 1.5
    const rows = []
    pts.slice().sort((a,b)=>a.cy-b.cy).forEach(p => {
      let placed = false
      for (const r of rows) {
        const avg = r.reduce((s,x)=>s + x.cy, 0) / r.length
        if (Math.abs(p.cy - avg) <= rowThreshold) { r.push(p); placed = true; break }
      }
      if (!placed) rows.push([p])
    })

    // columns: cluster x across all points
    const colThreshold = medianW * 1.5
    const cols = []
    pts.slice().sort((a,b)=>a.cx-b.cx).forEach(p => {
      let placed = false
      for (const c of cols) {
        const avg = c.reduce((s,x)=>s + x.cx, 0) / c.length
        if (Math.abs(p.cx - avg) <= colThreshold) { c.push(p); placed = true; break }
      }
      if (!placed) cols.push([p])
    })

    // compute column centers
    const colCenters = cols.map(c => c.reduce((s,x)=>s + x.cx, 0) / c.length).sort((a,b)=>a-b)

    // build grid rows x colsCount
    const colsCount = Math.max(1, colCenters.length)
    const grid = rows.slice().sort((a,b)=> (a.reduce((s,x)=>s + x.cy,0)/a.length) - (b.reduce((s,x)=>s + x.cy,0)/b.length)).map(r => {
      const rowArr = Array.from({ length: colsCount }, () => null)
      r.forEach(cell => {
        // find closest column
        let bestIdx = 0
        let bestDist = Infinity
        for (let i = 0; i < colCenters.length; i++) {
          const d = Math.abs(colCenters[i] - cell.cx)
          if (d < bestDist) { bestDist = d; bestIdx = i }
        }
        rowArr[bestIdx] = cell.num || null
        // annotate the cell with row/col for rendering
        cell.col = bestIdx
      })
      return rowArr
    })

    // annotate row index on pts
    const annotated = []
    rows.forEach((r, ri) => r.forEach(p => annotated.push({ ...p, row: ri, col: p.col })) )

    // compute row centers
    const rowCenters = rows.map(r => r.reduce((s,x)=>s + x.cy,0) / r.length)

    return { grid, annotated, rowCenters, colCenters }
  }

  // Called when OCR or manual edit produces a new card matrix.
  const setCardFromOCR = ({ rows, boxes: ocrBoxes, imageUrl: imgUrl }) => {
    // If rows were extracted from text, prefer them (simple case)
    if (rows && rows.length > 0) {
      setCard(rows)
      setBoxes(ocrBoxes)
      setImageUrl(imgUrl)
      // reset dabbed when new card loaded
      const newDab = Array.from({ length: rows.length }, () => Array.from({ length: rows[0].length }, () => false))
      setDabbed(newDab)
      setPlayers(Array.from({ length: rows.length }, (_, i) => `Player ${i + 1}`))
      setCalled([])
      setCurrentBall(null)
      setWinnerRow(null)
      return
    }

    // Try to compute layout from bounding boxes (image-based detection)
    const layout = computeGridFromBoxes(ocrBoxes)
    if (layout && layout.grid && layout.grid.length > 0) {
      setCard(layout.grid)
      setBoxes(layout.annotated)
      setImageUrl(imgUrl)
      const newDab = Array.from({ length: layout.grid.length }, () => Array.from({ length: layout.grid[0].length }, () => false))
      setDabbed(newDab)
      setPlayers(Array.from({ length: layout.grid.length }, (_, i) => `Player ${i + 1}`))
      setCalled([])
      setCurrentBall(null)
      setWinnerRow(null)
      return
    }

    // Fallback: no structured rows, just set empty or text-derived
    const fallback = rows || defaultEmptyCard()
    setCard(fallback)
    setBoxes(ocrBoxes)
    setImageUrl(imgUrl)
    const newDab2 = Array.from({ length: fallback.length }, () => Array.from({ length: fallback[0].length }, () => false))
    setDabbed(newDab2)
    setPlayers(Array.from({ length: fallback.length }, (_, i) => `Player ${i + 1}`))
    setCalled([])
    setCurrentBall(null)
    setWinnerRow(null)
  }

  // toggle dab for a specific cell (manual)
  const toggleDab = (r, c) => {
    setDabbed(prev => {
      const copy = prev.map(row => row.slice())
      copy[r][c] = !copy[r][c]
      return copy
    })
  }

  // When a number is called, mark any matching numbers on the card as dabbed (auto)
  const handleNumberCalled = useCallback((num, { auto } = { auto: false }) => {
    setCalled(prev => {
      const next = [...prev, num]
      return next
    })
    setCurrentBall(num)
    if (auto) {
      setDabbed(prev => {
        const copy = prev.map(row => row.slice())
        for (let r = 0; r < card.length; r++) {
          for (let c = 0; c < (card[r] || []).length; c++) {
            if (String(card[r][c]) === String(num)) copy[r][c] = true
          }
        }
        return copy
      })
    }
  }, [card])

  // On any dab change, check for full-row wins
  useEffect(() => {
    if (!dabbed) return
    for (let r = 0; r < dabbed.length; r++) {
      const row = dabbed[r]
      const all = row.every(Boolean)
      if (all) {
        setWinnerRow(r)
        fireConfetti()
        break
      }
    }
  }, [dabbed])

  const resetGame = () => {
    setCalled([])
    setCurrentBall(null)
    setDabbed(prev => prev.map(row => row.map(() => false)))
    setWinnerRow(null)
  }

  return (
    <div className="app-root">
      <header className="topbar">
        <h1>Custom Bingo</h1>
      </header>
      <main className="main">
        <section className="left">
          <ImageUploader onResult={setCardFromOCR} />
          <PlayerList players={players} setPlayers={setPlayers} />
          <BallPicker
            maxBall={settings.maxBall}
            setMaxBall={(v) => setSettings(s => ({ ...s, maxBall: v }))}
            autoMode={settings.autoMode}
            setAutoMode={(v) => setSettings(s => ({ ...s, autoMode: v }))}
            called={called}
            onCall={(num) => handleNumberCalled(num, { auto: settings.autoMode })}
            resetGame={resetGame}
            currentBall={currentBall}
          />
        </section>
        <section className="right">
          <CardView
            card={card}
            players={players}
            setPlayers={setPlayers}
            dabbed={dabbed}
            onToggleDab={toggleDab}
            manualMode={!settings.autoMode}
            setCard={setCard}
            setDabbed={setDabbed}
            boxes={boxes}
            imageUrl={imageUrl}
            setBoxes={setBoxes}
            setImageUrl={setImageUrl}
          />
        </section>
      </main>
      <WinModal
        open={winnerRow !== null}
        onClose={() => setWinnerRow(null)}
        winner={{ row: winnerRow, player: players[winnerRow] }}
      />
    </div>
  )
}
