/*
  src/components/PlayerList.js
  Simple list to name players (one per row). Names persist via parent state.
*/
import React from 'react'

export default function PlayerList({ players, setPlayers }){
  if(!players) return null

  const update = (i, val) => {
    const next = players.slice()
    next[i] = val
    setPlayers(next)
  }

  return (
    <div className="controls">
      <h3>Players</h3>
      {players.map((p,i)=> (
        <div key={i} style={{display:'flex',gap:8,alignItems:'center',marginBottom:6}}>
          <label style={{width:60}}>Row {i+1}</label>
          <input value={p} onChange={(e)=>update(i,e.target.value)} />
        </div>
      ))}
    </div>
  )
}
