/*
  src/components/CardView.js
  Renders the bingo card as an image with overlaid clickable number boxes.
  Falls back to grid view if no image.
*/
import React, { useState, useRef, useEffect } from 'react' 

export default function CardView({ card, players, setPlayers, dabbed, onToggleDab, onToggleDabLine, manualMode, setCard, setDabbed, imageUrl, grid }){
  const [editing, setEditing] = useState(null) // {r,c} for editing

  // image overlay refs/state (moved out of conditional to preserve hooks order)
  const imageContainerRef = useRef(null)
  const imgRef = useRef(null)
  const [imgRect, setImgRect] = useState(null)
  const [containerRect, setContainerRect] = useState(null)

  useEffect(()=>{
    const updateRect = () => {
      if (!imageContainerRef.current) return
      const c = imageContainerRef.current.getBoundingClientRect()
      setContainerRect(c)
      if (imgRef.current) {
        const ir = imgRef.current.getBoundingClientRect()
        setImgRect(ir)
      } else {
        setImgRect(c)
      }
    }
    // only add listener when we actually have an image URL
    updateRect()
    window.addEventListener('resize', updateRect)
    return () => window.removeEventListener('resize', updateRect)
  }, [imageUrl])

  if (!card) return <div className="card-view">No card loaded.</div>

  const rows = card.length
  const cols = card[0]?.length || 0

  const updateNumber = (r,c,val) => {
    const next = card.map(row=>row.slice())
    next[r][c] = val === '' ? null : isNaN(Number(val)) ? val : Number(val)
    setCard(next)
  }

  const toggleDabForNumber = (num) => {
    // Find position of num in card
    for (let r = 0; r < card.length; r++) {
      for (let c = 0; c < card[r].length; c++) {
        if (String(card[r][c]) === String(num)) {
          onToggleDab(r, c)
          return
        }
      }
    }
  }

  if (imageUrl) {
    // Image view with optional overlay (grid)
    const containerStyle = {position:'relative'}

    // compute overlay cell positions based on grid bbox percentages
    const renderOverlay = () => {
      if (!imgRect || !containerRect || !imageUrl || !grid) return null
      const bbox = grid.bbox
      if (!bbox) return null
      // compute overlay left/top relative to container rect (we position overlay inside container)
      const offsetLeft = imgRect.left - containerRect.left
      const offsetTop = imgRect.top - containerRect.top
      const x0 = offsetLeft + bbox.x0p * imgRect.width
      const y0 = offsetTop + bbox.y0p * imgRect.height
      const width = (bbox.x1p - bbox.x0p) * imgRect.width
      const height = (bbox.y1p - bbox.y0p) * imgRect.height
      const cellW = width / grid.cols
      const cellH = height / grid.rows
      // overlay container positioned relative to image container
      return (
        <div className="card-overlay" style={{position:'absolute',left:x0,top:y0,width:width,height:height,pointerEvents:'auto'}}>
          {Array.from({length: grid.rows}).map((_, r) => (
            <div key={r} style={{position:'relative'}}>
              {Array.from({length: grid.cols}).map((_, c) => {
                const left = c * cellW
                const top = r * cellH
                const isDab = dabbed?.[r]?.[c]
                return (
                  <div
                    key={c}
                    className={`card-overlay-cell ${isDab ? 'dab' : ''}`}
                    onClick={(e) => { e.stopPropagation(); if (manualMode) onToggleDab(r,c) }}
                    style={{position:'absolute',left:left,top:top,width:Math.max(0,Math.floor(cellW)),height:Math.max(0,Math.floor(cellH)),pointerEvents:'auto'}}
                  />
                )
              })}
            </div>
          ))}

          {/* row player labels (to left of overlay, clamped inside container) */}
          {grid.playerOverlay?.rows && grid.playerOverlay.rows.map((name, r) => {
            const centerY = r * cellH + cellH / 2
            const labelH = 22
            const topPos = y0 + centerY - labelH / 2
            const rowOffsetPct = (grid.playerOverlay?.rowOffsetPct != null) ? Number(grid.playerOverlay.rowOffsetPct) : null
            const rowOffsetFallback = (grid.playerOverlay?.rowOffset != null) ? Number(grid.playerOverlay.rowOffset) : 110
            const rowOffsetPx = rowOffsetPct != null ? Math.round(rowOffsetPct * width) : rowOffsetFallback
            const leftPos = Math.max(4, x0 - rowOffsetPx)
            return (
              <div key={'prow'+r} className="player-label row" onClick={(e)=>{ e.stopPropagation(); onToggleDabLine && onToggleDabLine('row', r) }} style={{position:'absolute',left:leftPos,top:topPos,width:100,height:labelH,display:'flex',alignItems:'center',padding:'4px 6px',background:'rgba(255,255,255,0.9)',color:'#012',cursor:'pointer',borderRadius:4}}>{name}</div>
            )
          })}

          {/* column player labels (above overlay, rotated) */}
          {grid.playerOverlay?.cols && grid.playerOverlay.cols.map((name, c) => {
            const centerX = c * cellW + cellW / 2
            const labelW = 22
            const colOffsetPct = (grid.playerOverlay?.colOffsetPct != null) ? Number(grid.playerOverlay.colOffsetPct) : null
            const colOffsetFallback = (grid.playerOverlay?.colOffset != null) ? Number(grid.playerOverlay.colOffset) : 36
            const colOffsetPx = colOffsetPct != null ? Math.round(colOffsetPct * height) : colOffsetFallback
            const topPos = Math.max(4, y0 - colOffsetPx)
            const leftPos = x0 + centerX - labelW / 2
            return (
              <div key={'pcol'+c} className="player-label col" onClick={(e)=>{ e.stopPropagation(); onToggleDabLine && onToggleDabLine('col', c) }} style={{position:'absolute',left:leftPos,top:topPos,width:labelW,height:100,display:'flex',alignItems:'center',justifyContent:'center',padding:'4px 6px',background:'rgba(255,255,255,0.9)',color:'#012',cursor:'pointer',borderRadius:4,transform:'rotate(-90deg)'}}>{name}</div>
            )
          })}

        </div>
      )
    }

    return (
      <div className="card-view">
        <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
          <h3>Card View</h3>
          <div style={{fontSize:12,color:'#666'}}>Click numbers in the grid below to dab (manual)</div>
        </div>
        <div style={{marginTop:8}}>
          <div ref={imageContainerRef} style={containerStyle}>
            <img ref={imgRef} src={imageUrl} alt="Bingo Card" style={{maxWidth:'100%', height:'auto', display:'block', border: '1px solid #ddd'}} onLoad={()=>{
              if (!imageContainerRef.current) return
              const r = imageContainerRef.current.getBoundingClientRect()
              setImgRect(r)
            }} />
            {renderOverlay()}
          </div>
        </div>
        {/* show the editable grid below for manual dabbing */}
        <div style={{marginTop:12}}>
          {card.map((row, r) => (
            <div key={r} style={{marginBottom:6}}>
              <div className="player-row">
                <strong style={{width:90}}>{players?.[r] || `Row ${r+1}`}</strong>
                <div style={{flex:1,display:'grid',gridTemplateColumns:`repeat(${cols},1fr)`,gap:6}}>
                  {row.map((cell, c) => (
                    <div key={c} className={`cell ${dabbed?.[r]?.[c] ? 'dab' : ''}`} onClick={()=>{ if (manualMode) onToggleDab(r,c) }}>
                      <div>
                        <input value={cell ?? ''} onChange={(e)=>updateNumber(r,c,e.target.value)} style={{width:'100%',border:'none',background:'transparent',textAlign:'center'}} />
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    )
  }
  // Simple grid view (no automatic overlays or player detection)
  return (
    <div className="card-view">
      <div style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
        <h3>Card View</h3>
        <div style={{fontSize:12,color:'#666'}}>Tap numbers to dab (manual)</div>
      </div>
      <div style={{marginTop:8}}>
        {card.map((row, r) => (
          <div key={r} style={{marginBottom:6}}>
            <div className="player-row">
              <strong style={{width:90}}>{players?.[r] || `Row ${r+1}`}</strong>
              <div style={{flex:1,display:'grid',gridTemplateColumns:`repeat(${cols},1fr)`,gap:6}}>
                {row.map((cell, c) => (
                  <div key={c} className={`cell ${dabbed?.[r]?.[c] ? 'dab' : ''}`} onClick={()=>{ if (manualMode) onToggleDab(r,c) }}>
                    <div>
                      <input value={cell ?? ''} onChange={(e)=>updateNumber(r,c,e.target.value)} style={{width:'100%',border:'none',background:'transparent',textAlign:'center'}} />
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}
